targets.expr:
(
  (node_filesystem_size_bytes{instance=~"$node", job="$job", mountpoint="/", fstype!="rootfs"}
   - node_filesystem_avail_bytes{instance=~"$node", job="$job", mountpoint="/", fstype!="rootfs"})
  / node_filesystem_size_bytes{instance=~"$node", job="$job", mountpoint="/", fstype!="rootfs"}
) * 100

(
  (node_filesystem_size_bytes{instance=~"$node", job="$job", mountpoint="/", fstype!="rootfs"}
   - node_filesystem_avail_bytes{instance=~"$node", job="$job", mountpoint="/", fstype!="rootfs"})
  / node_filesystem_size_bytes{instance=~"$node", job="$job", mountpoint="/", fstype!="rootfs"}
) * 100

max by (instance) (
  node_hwmon_temp_celsius{instance=~"$node", job=~"$job"} 
